+					? state.auth.user.permissions.map((p) => p.toLowerCase())
+					: [],
+		}),
+		$_hasPermission() {
+			return typeof this.permission === "string"
+				? !this.permission ||
+						this.userPermissions.includes(this.permission.toLowerCase())
+				: !this.permission() || this.permission(this.userPermissions);
+		},
+	},
+};

